namespace Lof\Auction\Controller\Adminhtml\Product;

use Lof\Auction\Model\ResourceModel\Product\CollectionFactory;
use Lof\Auction\Model\ResourceModel\Product\Source\AuctionStatus;
use Magento\Backend\App\Action;
use Magento\Backend\App\Action\Context;
use Magento\Backend\Model\View\Result\Redirect;
use Magento\Framework\Controller\ResultFactory;
use Magento\Ui\Component\MassAction\Filter;

/**
 * Class MassCancel
 * @package Lof\Auction\Controller\Adminhtml\Product
 */
class MassCancel extends Action
{
    /**
     * @var Filter
     */
    protected $filter;

    /**
     * @var CollectionFactory
     */
    protected $collectionFactory;

    /**
     * @param Context $context
     * @param Filter $filter
     * @param CollectionFactory $collectionFactory
     */
    public function __construct(
        Context $context,
        Filter $filter,
        CollectionFactory $collectionFactory
    ) {
        $this->filter = $filter;
        $this->collectionFactory = $collectionFactory;
        parent::__construct($context);
    }

    /**
     * Execute action
     *
     * @return Redirect
     * @throws \Magento\Framework\Exception\LocalizedException|\Exception
     */
    public function execute()
    {
        $collection = $this->filter->getCollection($this->collectionFactory->create());
        $collection->addFieldToFilter("auction_status", [
                "in" => [
                    AuctionStatus::STATUS_PROCESS,
                    AuctionStatus::STATUS_WINNER_NOT_DEFINED
                ]
            ]);

        $collectionSize = $collection->getSize();

        foreach ($collection as $product) {
            $product->setAuctionStatus(AuctionStatus::STATUS_CANCELED);
            $product->save();
        }

        $this->messageManager->addSuccess(__('A total of %1 record(s) have been canceled.', $collectionSize));

        /** @var Redirect $resultRedirect */
        $resultRedirect = $this->resultFactory->create(ResultFactory::TYPE_REDIRECT);
        return $resultRedirect->setPath('*/*/');
    }

    /**
     * Check the permission to run it
     *
     * @return bool
     */
    protected function _isAllowed()
    {
        return $this->_authorization->isAllowed('Lof_Auction::mass_cancel');
    }
}
