namespace Lof\MarketPlace\Block\Adminhtml;

class Message extends \Magento\Backend\Block\Widget\Grid\Container
{
    /**
     * Block constructor
     *
     * @return void
     */
    protected function _construct()
    {
        $this->_controller = 'adminhtml_message';
        $this->_blockGroup = 'Lof_MarketPlace';
        $this->_headerText = __('Message Seller');

        parent::_construct();

        if ($this->_isAllowedAction('Lof_MarketPlace::message_edit')) {
            $this->buttonList->update('add', 'label', __('Add New Message'));
        } else {
            $this->buttonList->remove('add');
        }
    }

    /**
     * Check permission for passed action
     *
     * @param string $resourceId
     * @return bool
     */
    protected function _isAllowedAction($resourceId)
    {
        return $this->_authorization->isAllowed($resourceId);
    }
}
