namespace Lof\MarketPlace\Model\Config\Source;

class RatingSorting implements \Magento\Framework\Option\ArrayInterface
{
    /**
     * Options getter
     *
     * @return array
     */
    public function toOptionArray()
    {
        return [
            ['value' => 'latest', 'label' => __('Latest First')],
            ['value' => 'oldest', 'label' => __('Oldest First')],
            ['value' => 'bestRating', 'label' => __('Best Ratings First')],
            ['value' => 'helpful', 'label' => __('Helpful First')],
            ['value' => 'verified', 'label' => __('Verified Buyer First')],
            ['value' => 'recommended', 'label' => __('Recommended First')]
        ];
    }

    /**
     * Get options in "key-value" format
     *
     * @return array
     */
    public function toArray()
    {
        $array = [];
        foreach ($this->toOptionArray() as $item) {
            $array[$item['value']] = $item['label'];
        }
        return $array;
    }
}
