namespace Lof\MarketPlace\Ui\DataProvider\Seller;

use Lof\MarketPlace\Model\ResourceModel\SellerProduct\CollectionFactory;
use Lof\MarketPlace\Model\Source\Approval;

class SellerProductPendingDataProvider extends \Lof\MarketPlace\Ui\DataProvider\Seller\SellerProductDataProvider
{
    /**
     * SellerProductPendingDataProvider constructor.
     * @param string $name
     * @param string $primaryFieldName
     * @param string $requestFieldName
     * @param CollectionFactory $collectionFactory
     * @param array $addFieldStrategies
     * @param array $addFilterStrategies
     * @param array $meta
     * @param array $data
     */
    public function __construct(
        $name,
        $primaryFieldName,
        $requestFieldName,
        CollectionFactory $collectionFactory,
        array $addFieldStrategies = [],
        array $addFilterStrategies = [],
        array $meta = [],
        array $data = []
    ) {
        parent::__construct(
            $name,
            $primaryFieldName,
            $requestFieldName,
            $collectionFactory,
            $addFieldStrategies,
            $addFilterStrategies,
            $meta,
            $data
        );
        $this->collection->addFieldToFilter('status', Approval::STATUS_PENDING);
    }
}
