namespace Lofmp\PriceComparison\Controller\Adminhtml\Product;

class Delete extends \Lofmp\PriceComparison\Controller\Adminhtml\Product
{
    /**
     * {@inheritdoc}
     */
    protected function _isAllowed()
    {
        return $this->_authorization->isAllowed('Lofmp_PriceComparison::product_delete');
    }

    /**
     * Delete action
     *
     * @return \Magento\Backend\Model\View\Result\Redirect
     */
    public function execute()
    {
        // check if we know what should be deleted
        $id = $this->getRequest()->getParam('id');
        /** @var \Magento\Backend\Model\View\Result\Redirect $resultRedirect */
        $resultRedirect = $this->resultRedirectFactory->create();

        if ($id) {
            try {
                // init model and delete
                $model = $this->_objectManager->create('Lofmp\PriceComparison\Model\Product');
                $model->load($id);
                $model->delete();
                // display success message
                $this->messageManager->addSuccess(__('The product has been deleted.'));
                return $resultRedirect->setPath('*/*/');
            } catch (\Exception $e) {
                // display error message
                $this->messageManager->addError($e->getMessage());
                // go back to edit form
                return $resultRedirect->setPath('*/*/edit', ['id' => $id]);
            }
        }
        // display error message
        $this->messageManager->addError(__('We can\'t find a product to delete.'));
        // go to grid
        return $resultRedirect->setPath('*/*/');
    }
}
