namespace Lofmp\StoreLocator\Block\Adminhtml;

class Tag extends \Magento\Backend\Block\Widget\Grid\Container
{
    /**
     * Block constructor
     *
     * @return void
     */
    protected function _construct()
    {
        $this->_controller = 'adminhtml_tag';
        $this->_blockGroup = 'Lofmp_StoreLocator';
        $this->_headerText = __('Manage Tags');

        parent::_construct();

        if ($this->_isAllowedAction('Lofmp_StoreLocator::save')) {
            $this->buttonList->update('add', 'label', __('Add New Tag'));
        } else {
            $this->buttonList->remove('add');
        }
    }

    /**
     * Check permission for passed action
     *
     * @param string $resourceId
     * @return bool
     */
    protected function _isAllowedAction($resourceId)
    {
        return $this->_authorization->isAllowed($resourceId);
    }
}